.print-format table, .print-format tr, 
.print-format td, .print-format div, .print-format p {
	vertical-align: middle;
}
@media screen {
	.print-format {
		width: 104mm;
		min-height: 40mm;
		height: 40mm;
		padding: 0;
	}
}

.custom {
	width:100%;
	border:none;
}

.custom td {
	width : 50%;
	height : 40mm;
	padding : 2mm !important;
}
p sup{
	opacity: 0.7;
}
